craft.script:
function main(params){
    var slope = params.slope
        ,y_inter = params.intercept

    var x_axis = cube([0.2,10,0.5]).translate([0,-5,0])
        ,y_axis = cube([10,0.2,0.5]).translate([-5,0,0])
        ,coordinate = union(
            x_axis
            ,y_axis
        )
    var random_x1 = 2 //coordinate boundary
        ,random_x2 = -2;

    var p1 = {
        x: random_x1, //random x
        y: slope*random_x1 + y_inter
    }, p2 = {
        x: random_x2, //random x
        y: slope*random_x2 + y_inter
    }

    var line = rectangular_extrude([[p1.x, p1.y],[p2.x,p2.y]], // path is an array of 2d coords
                                        {w: 0.1, h: 1, closed: false});
    return union(
        coordinate,
        line
    )

}
